INSERT INTO `news` (`id_news`, `name_news`, `descript_news`, `image_news`, `uploaded_news`) VALUES
	(7, 'จัดกิจกรรมเสริงสร้างทักษะสำหรับหลักสูตร', 'ในวันที่ 26 ตุลาคม, สถาบันการศึกษา มหาลัยบ้านสมเด็จเจ้าพระยา ได้จัดกิจกรรมเสริมสร้างทักษะที่ท้าทายและน่าสนใจสำหรับนักเรียนทุกระดับชั้น ในโครงการที่ได้รับการสนับสนุนจาก [บริษัทหรือองค์กรที่สนับสนุน] นักเรียนได้มีโอกาสเข้าร่วมกิจกรรมที่ท้าทายและสร้างประสบการณ์ที่จะอยู่ในหลักสูตรการศึกษาของตนเองในอนาคต กิจกรรมในวันนี้เน้นไปที่การพัฒนาทักษะทางการศึกษาและทักษะทางอารมณ์ โดยมีกิจกรรมอย่างคีบตุ๊กตาสมุด, การศึกษาเกี่ยวกับสิ่งแวดล้อมในธรรมชาติ, การบูรณาการทางวัฒนธรรม และการสร้างนิสัยที่ดีในชีวิตประจำวัน  นอกจากนี้ยังมีการจัดกิจกรรมสุขภาพที่เน้นการออกกำลังกายและการดูแลสุขภาพให้เหมาะสม ที่จะช่วยเสริมสร้างร่างกายแข็งแรงและสุขภาพที่ดี นอกจากนี้ยังมีการจัดสร้างพื้นที่เพื่อเรียนรู้และสร้างสรรค์ เพื่อให้นักเรียนได้มีโอกาสพัฒนาความคิดสร้างสรรค์และความคิดนวัตกรรมในการแก้ปัญหาในชีวิตประจำวัน [ชื่อผู้บรรยายหรือผู้นำกิจกรรม] ผู้บรรยายในกิจกรรมได้กล่าวถึงความสำคัญของการศึกษาและการพัฒนาทักษะที่เพื่อนำไปสู่อนาคตที่ดีของนักเรียน และให้กำลังใจให้กับนักเรียนในการมีความมุ่งมั่นในการเรียนรู้และการพัฒนาตนเอง กิจกรรมนี้เป็นตัวอย่างดีของการ', '341776615_580669607364042_2887073807516276571_n.jpg', '2023-10-26 17:10:59'),
